import React from 'react';
import resumeData from '../data/resumeData';

function ResumeSection() {
  const { summary, experience, education, topSkills, certifications } = resumeData;

  return (
    <section className="section" id="resume">
      <div className="container">
        <div className="resume-panel">
          <h2 className="title is-size-3 mb-4" style={{ color: '#fff' }}>Resume</h2>

          {/* Summary */}
          <h3 className="is-size-4">Summary</h3>
          <p className="mb-4" style={{ whiteSpace: 'pre-line' }}>
            {summary}
          </p>

          {/* Experience */}
          <h3 className="is-size-4">Experience</h3>
          {experience.map((exp, idx) => (
            <div key={idx} className="mb-3" style={{ borderBottom: '1px solid #444', paddingBottom: '1rem' }}>
              <strong>{exp.company}</strong><br />
              <em>{exp.role}</em><br />
              <span>{exp.dateRange}</span><br />
              {exp.location && <span>{exp.location}</span>}<br />
              {exp.description && (
                <p style={{ marginTop: '0.5rem', whiteSpace: 'pre-line' }}>{exp.description}</p>
              )}
            </div>
          ))}

          {/* Education */}
          <h3 className="is-size-4 mt-5">Education</h3>
          {education.map((edu, idx) => (
            <div key={idx} className="mb-3" style={{ borderBottom: '1px solid #444', paddingBottom: '1rem' }}>
              <strong>{edu.institution}</strong><br />
              <em>{edu.degree}</em><br />
              <span>{edu.dateRange}</span><br />
            </div>
          ))}

          {/* Skills & Certifications (Optional) */}
          <div className="columns mt-5">
            <div className="column">
              <h4 className="is-size-5">Top Skills</h4>
              <ul>
                {topSkills.map((skill, idx) => (
                  <li key={idx} style={{ listStyleType: 'circle', marginLeft: '1rem' }}>
                    {skill}
                  </li>
                ))}
              </ul>
            </div>
            <div className="column">
              <h4 className="is-size-5">Certifications</h4>
              <ul>
                {certifications.map((cert, idx) => (
                  <li key={idx} style={{ listStyleType: 'circle', marginLeft: '1rem' }}>
                    {cert}
                  </li>
                ))}
              </ul>
            </div>
          </div>

        </div>
      </div>
    </section>
  );
}

export default ResumeSection;
