import React, { useState } from 'react';
import Navbar from './components/Navbar';
import HeroSection from './components/HeroSection';
import ProjectsLayout from './components/ProjectsLayout';
import ResumeStepper from './components/ResumeStepper';
import SideConsole from './components/SideConsole';
import Footer from './components/Footer';

function App() {
  // We'll handle dark mode by toggling a class on <html>
  const [isDarkMode, setIsDarkMode] = useState(true);

  const handleDarkModeToggle = () => {
    setIsDarkMode(!isDarkMode);
    // Toggle a class on the <html> root
    document.documentElement.classList.toggle('dark-mode');
  };

  return (
    <>
      <Navbar 
        isDarkMode={isDarkMode} 
        onDarkModeToggle={handleDarkModeToggle} 
      />
      <HeroSection />

      <div className="columns is-gapless my-app-layout">
        <div className="column is-8">
          <ProjectsLayout />
          <ResumeStepper />
        </div>
        <div className="column is-4">
          <SideConsole />
        </div>
      </div>

      <Footer />
    </>
  );
}

export default App;
